import React from "react";
import "./AdminMain.css";
import TextField from "@material-ui/core/TextField";
import { useHistory } from "react-router-dom";
import Submit from "./SubmitAdminMainDpt";
export default function AdminMain() {
  const history = useHistory();
  const [credentials, setCredentials] = React.useState({
    email: "",
    uid:"",
    name:"",
    password: "",
    confirmPassword: "",
    did:""
  });
  const [disabled, setDisabled] = React.useState(true);
  const [error,setError]=React.useState({invalidEmail:false});
  const [invalidPassword,setInvalidPassword]=React.useState(false);

  function handleChange(e) {
    const { name, value } = e.target;
    if(error.invalidEmail){
      setError({
        invalidEmail:false
      })
    }

    setCredentials((prev) => {
      return {
        ...prev,
        [name]: value
      };
    });
  }

  function errorLogging(value){
    if(value==="Success"){
       setError({
         invalidEmail:false
       });

     history.push('/login');
    }
    else if(value==="Email Already exist"){
       setError({
         invalidEmail:true
       })
    }
  }

  return (
    <div className="admin-main-body">
      <div className="admin-main-container">
        <div className="admin-main-logo">W</div>
        <h1 className="admin-main-signUp">Add Department</h1>
        <TextField
          name="college"
          value={credentials.college}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>Department Name</span>
          }
          onChange={handleChange}
          className="register-password"
          style={{marginTop:"20px"}}
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />
        <TextField
          name="college"
          value={credentials.college}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>Department ID</span>
          }
          onChange={handleChange}
          className="register-password"
          style={{marginTop:"20px"}}
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />

        <div style={{ width: "85%", marginLeft: "7.5%", marginTop: "40px" }}>
          <Submit disabled={disabled} credentials={credentials}  errorLogging={errorLogging}/>
        </div>
        <div
          onClick={() => {
            history.push("/");
          }}
          className="admin-main-go-home"
          style={{ marginTop: "20px",cursor:"pointer" }}
        >
          Go to home
        </div>
      </div>
    </div>
  );
}
