import React from "react";
import Login from "./components/Login";
import Register from "./components/Register";
import RegisterCollege from "./components/RegisterCollege";
import Home from "./components/Home";
import College from "./components/College";
import { Switch } from "react-router-dom";
import { BrowserRouter as Router } from "react-router-dom";
import Profile from "./components/Profile";
import PrivateRoute from "./components/PrivateRoute";
import Dept from "./components/Department";
import PageNotFound from "./components/PageNotFound";
import { Route } from "react-router-dom";
import AdminCollege from "./components/AdminCollege";
import Loading from "./components/Loading";
import AdminMain from "./components/AdminMain";
import Sidebar from "./components/Sidebar";
import {
  isAdminMain,
  isAdminCollege,
  isTeacher,
  isHod
} from "./components/Util";

export default function App() {
  const [dept,setDeptList]=React.useState([]);
  React.useEffect(()=>{
    fetch("/getDepartment").then(response=>response.json()).then
     (data=>{
      const arr=[]
      console.log(data.dept);
      data.dept.map((item,index)=>
      arr.push(item[0])
      )
      setDeptList(arr);
      localStorage.setItem("ListDept",arr);
  });
  },[]);

  console.log(localStorage.getItem("ListDept"));
  return (
    <div style={{ height: "100%" }}>
      <Router>
        <Switch>
          <Sidebar/>
          <table style={{opacity:!loading}}>
          <tr>
          <td style={{width:"275px"}}>
            <Sidebar dept={dept} />
          </td>
          <td colspan="15">v
          </td>
        </tr>
      </table>
        </Switch>
      </Router>
    </div>
  );
}
