import React from "react";
import "./AdminMain.css";
import TextField from "@material-ui/core/TextField";
import { useHistory } from "react-router-dom";
import MenuItem from '@material-ui/core/MenuItem';
import FormHelperText from '@material-ui/core/FormHelperText';
import FormControl from '@material-ui/core/FormControl';
import Select from '@material-ui/core/Select';
import Submit from "./SubmitAdminMain";
export default function AdminMain(props) {
  const history = useHistory();
  const [credentials, setCredentials] = React.useState({
    email: "",
    uid:"",
    name:"",
    password: "",
    confirmPassword: "",
    did:""
  });
  const [disabled, setDisabled] = React.useState(true);
  const [error,setError]=React.useState({invalidEmail:false});
  const [invalidPassword,setInvalidPassword]=React.useState(false);
  const [success,setSuccess]=React.useState(false);
  React.useEffect(() => {
    const res = () => {
      const mailFormat = /^([a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(\.[a-zA-Z0-9-]{2,4})+)*$/;
      if (
        credentials.email.match(mailFormat) &&
        credentials.password.length > 1 &&
        credentials.confirmPassword.length === credentials.password.length
      )
        return false;
      return true;
    };
    const fans = res();
    setDisabled(fans);
    if(credentials.password.length === credentials.confirmPassword.length  && credentials.confirmPassword !== credentials.password){
      setInvalidPassword(true);
    }
    else{
      setInvalidPassword(false);
    }
  }, [credentials]);

  React.useEffect(()=>{
    fetch("/addHOD").then(res=>{
       return res.json()
    }).then(json=>{
      if(json.status!=="Success")
      setSuccess(false);
      else
      setSuccess(true);
      timer.current = window.setTimeout(() => {
        setLoading(false);
        props.errorLogging(json.status);
      }, 2000);
    })
  },[]);
  function handleChange(e) {
    const { name, value } = e.target;
    if(error.invalidEmail){
      setError({
        invalidEmail:false
      })
    }

    setCredentials((prev) => {
      return {
        ...prev,
        [name]: value
      };
    });
  }

  function errorLogging(value){
    if(value==="Success"){
       setError({
         invalidEmail:false
       });

     history.push('/login');
    }
    else if(value==="Email Already exist"){
       setError({
         invalidEmail:true
       })
    }
  }

  return (
    <div className="admin-main-body">
      <div className="admin-main-container" style={{marginTop:"2%"}}>
        <div className="admin-main-logo">W</div>
        <h1 className="admin-main-signUp">Add Admin Department</h1>
        {error.invalidEmail ?
           <TextField
            error
             name="email"
             value={credentials.email}
             label={
               <span style={{ fontFamily: '"Nunito", sans-serif' }}>Email already registered</span>
             }
             onChange={handleChange}
             className="register-email"
             inputProps={{
               min: 0,
               style: { fontFamily: '"Nunito", sans-serif' }
             }}
        />
        :
        <TextField
          name="email"
          value={credentials.email}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>Admin Email</span>
          }
          onChange={handleChange}
          className="register-email"
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />}

         <TextField
          name="name"
          value={credentials.name}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>Admin Name</span>
          }
          onChange={handleChange}
          className="register-password"
          style={{marginTop:"20px"}}
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />

         <TextField
          name="name"
          value={credentials.name}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>Admin Department</span>
          }
          onChange={handleChange}
          className="register-password"
          style={{marginTop:"20px"}}
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />

        <div style={{ width: "85%", marginLeft: "7.5%", marginTop: "40px" }}>
          <Submit disabled={disabled} credentials={credentials}  errorLogging={errorLogging}/>
        </div>
        <div
          onClick={() => {
            history.push("/admin-main");
          }}
          className="admin-main-go-home"
          style={{ marginTop: "20px",cursor:"pointer" }}
        >
          Add HOD
        </div>
        <div
          onClick={() => {
            history.push("/admin-main-dpt");
          }}
          className="admin-main-go-home"
          style={{ marginTop: "20px",cursor:"pointer" }}
        >
          Add Department
        </div>
      </div>
    </div>
  );
}
