import React from "react";
import Sidebar from "./Sidebar";
import Homeright from "./Homeright";
import Loading from "./Loading";
import "./Home.css";
export default function Home() {
  const [dept,setDeptList]=React.useState([]); 
  const [materials,setMaterials]=React.useState([]); 
  const [loading,setLoading]=React.useState(true);
  const [logged,setLogged]=React.useState();

  React.useEffect(()=>{
    fetch("/getDepartment").then(response=>response.json()).then
     (data=>{
      const arr=[]
      console.log(data.dept);
      data.dept.map((item,index)=>
      arr.push(item[0])
      )
      console.log(arr);
      setDeptList(arr);
  })

  fetch("/home").then(response=>response.json()).then(data=>{
    setLogged(data.college_name)
    const arr=[]
    data.material.map((item,index)=>arr.push(
    {
      link:item[0],
      desc:item[1],
      type:item[2],
      faculty:item[3],
      subject:item[4],
      dept:item[5],
      date_added:item[6]
    }))
    console.log(arr);
    setMaterials(arr);
    setTimeout()
});
},[]);

  return (
    <div style={{ height: "100%" }}>
    {!loading ?
      <table style={{opacity:!loading}}>
        <tr>
          <td style={{width:"275px"}}>
            <Sidebar tobe="home" dept={dept} />
          </td>
          <td colspan="15">
            <Homeright materials={materials} logged={logged}/>
          </td>
        </tr>
      </table>
      : <Loading/>}
    </div>
  );
}
