import React from "react";
import "./AdminMain.css";
import TextField from "@material-ui/core/TextField";
import { useHistory } from "react-router-dom";
import Submit from "./SubmitAdminMain";

export default function AdminMain() {
  const history = useHistory();
  const [credentials, setCredentials] = React.useState({
    hodemail: "",
    hodname:"",
    dept:"",

  });
  const [disabled, setDisabled] = React.useState(true);
  const [error,setError]=React.useState({invalidEmail:false,invalidHod:false});
  React.useEffect(() => {
    const res = () => {
      const mailFormat = /^([a-zA-Z0-9.!#$%&'*+/=?^_`{|}~-]+@[a-zA-Z0-9-]+(\.[a-zA-Z0-9-]{2,4})+)*$/;
      if (credentials.hodemail.match(mailFormat))
        return false;
      return true;
    };
    const fans = res();
    setDisabled(fans);

  }, [credentials]);

  function handleChange(e) {
    const { name, value } = e.target;
    setCredentials((prev) => {
      return {
        ...prev,
        [name]: value
      };
    });
  }

  function errorLogging(value){
    if(value==="Success"){
       setError({
         invalidEmail:false
       });
       alert("HOD Successfully added")
    }
    else if(value==="Email Already exists"){
       setError({
         invalidEmail:true
       })
    }
    else if(value==="HOD Already exists"){
      setError({
        invalidHod:true
      })
    }
  }

  return (
    <div className="admin-main-body">
      <div className="admin-main-container" style={{marginTop:"2%"}}>
        <div className="admin-main-logo">W</div>
        <h1 className="admin-main-signUp">Add HOD</h1>
        {error.invalidEmail ?
           <TextField
            error
             name="email"
             value={credentials.hodemail}
             label={
               <span style={{ fontFamily: '"Nunito", sans-serif' }}>Email already registered</span>
             }
             onChange={handleChange}
             className="register-email"
             inputProps={{
               min: 0,
               style: { fontFamily: '"Nunito", sans-serif' }
             }}
        />
        :
        <TextField
          name="email"
          value={credentials.hodemail}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>HOD Email</span>
          }
          onChange={handleChange}
          className="register-email"
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />}

         <TextField
          name="name"
          value={credentials.name}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>HOD Name</span>
          }
          onChange={handleChange}
          className="register-password"
          style={{marginTop:"20px"}}
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        /> 
        {error.invalidHod ?       
         <TextField
          error
          name="name"
          value={credentials.dept}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>Department already has HOD</span>
          }
          onChange={handleChange}
          className="register-password"
          style={{marginTop:"20px"}}
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />:
        <TextField
          name="name"
          value={credentials.dept}
          label={
            <span style={{ fontFamily: '"Nunito", sans-serif' }}>HOD Department</span>
          }
          onChange={handleChange}
          className="register-password"
          style={{marginTop:"20px"}}
          inputProps={{
            min: 0,
            style: { fontFamily: '"Nunito", sans-serif' }
          }}
        />}

        <div style={{ width: "85%", marginLeft: "7.5%", marginTop: "40px" }}>
          <Submit disabled={disabled} credentials={credentials}  errorLogging={errorLogging}/>
        </div>
        <div
          onClick={() => {
            history.push("/admin-main-dpt");
          }}
          className="admin-main-go-home"
          style={{ marginTop: "20px",cursor:"pointer" }}
        >
          Add Department
        </div>
        <div
          onClick={() => {
            history.push("/admin-main-admin-dpt");
          }}
          className="admin-main-go-home"
          style={{ marginTop: "20px",cursor:"pointer" }}
        >
          Add Admin Department
        </div>
      </div>
    </div>
  );
}
