        <InputLabel id="demo-simple-select-filled-label">Section</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={section}
          onChange={handleChangeSection}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
        {sectionArr.map((item,index)=>{
          return(
          <MenuItem value={item.sectionname}>{item.sectionname}</MenuItem>
          )
        })}
        </Select>
        </FormControl>
        {!!sectionId &&
      <FormControl variant="filled" className={classes.formControl}>
        <InputLabel id="demo-simple-select-filled-label">Subject</InputLabel>
        <Select
          labelId="demo-simple-select-filled-label"
          id="demo-simple-select-filled"
          value={subject}
          onChange={handleChangeSubject}
        >
          <MenuItem value="">
            <em>None</em>
          </MenuItem>
          {subjectArr.map((item,index)=>{
          return(
          <MenuItem value={item.subjectcode}>{item.subjectcode}</MenuItem>
          )
        })}
        </Select>
      </FormControl>}

          <FormControl className={classes.formControl} style={{marginLeft:"5%"}}>
              <TextField
                id="date"
                label="Date"
                type="date"
                name="date"
                value={date}
                onChange={handleDateChange}
                defaultValue={new Date()}
                className={classes.textField}
                InputLabelProps={{
                  shrink: true,
                }}
              />
           </FormControl>
          <FormControl className={classes.formControl} style={{marginLeft:"5%"}}>
            <TextField
               id="time"
               label="Class Time"
               type="time"
               name="time"
                onChange={handleTimeChange}
               value={time}
               defaultValue="07:30"
               className={classes.textField}
               InputLabelProps={{
                  shrink: true,
               }}
               inputProps={{
                  step: 300, // 5 min
               }}
            />
           </FormControl>
         </DialogTitle>

         <DialogContent dividers style={{ backgroundColor: "#f2f2f0" }}>
         <div style={{ height: 400, width: '100%' }}>
             <DataGrid rows={rows} columns={columns} pageSize={5} checkboxSelection onSelectionChange={(newSelection) => {
             fetch("/attendance", {
              method:"POST",
              cache: "no-cache",
              headers:{
                  "content_type":"application/json",
              },
               body:JSON.stringify({cred:newSelection.rows})
              }
              ).then(response => {
                 return response.json()
              })
             .then(json => {  
               console.log(json) 
              })
         }}/>
          </div>
         </DialogContent>

         <DialogActions>
           <Button onClick={handleOk} color="primary">
             Ok
           </Button>
         </DialogActions>

       </Dialog>
     </div>
   );
 }

 ConfirmationDialogRaw.propTypes = {
   onClose: PropTypes.func.isRequired,
   open: PropTypes.bool.isRequired,
   value: PropTypes.string.isRequired
 };

 const useStyles = makeStyles((theme) => ({
   root: {
     width: "100%"
   },
   paper: {
     width: "80%",
     maxHeight: 435
   }
 }));

export default function Attendance(){
   const classes = useStyles();
   const [open, setOpen] = React.useState(false);
   const [value, setValue] = React.useState("Notes");

   const handleClickListItem = () => {
     setOpen(true);
   };

   const handleClose = (newValue) => {
     setOpen(false);

     if (newValue) {
       setValue(newValue);
     }
   };

   return(
      <div className={classes.root}>
      <IconButton onClick={handleClickListItem}>
      <AssignmentIcon  style={{
            fontSize: "25px",
            marginLeft: "10%",
            color: "#cd5d7d"
          }}/>
      </IconButton>
      <ConfirmationDialogRaw
        classes={{
          paper: classes.paper
        }}
        id="dialog-box"
        keepMounted
        open={open}
        onClose={handleClose}
        value={value}
      />
      </div>
   );
}
